class Solution {



      int SolveMemo(int index , vector<int>& days, vector<int>& costs,  vector<int> &dp)
    {
        if(index  == days.size())
            return 0;

        if(dp[index] != -1)
            return dp[index];

        //1 day Pass
        int op1 = costs[0] + SolveMemo(index+1 , days, costs, dp);

        //7 Day pass
        int i = index+1 ;
        while( i<days.size() && days[i]-days[index]<7 )
            i++;

        int op2 = costs[1] + SolveMemo(i , days, costs, dp);

        //7 Day pass
         while( i<days.size() && days[i]-days[index]<30 )
             i++;

        int op3 = costs[2] + SolveMemo(i , days, costs, dp);

        return dp[index] = min(op1 , min(op2, op3));
    }

    int SolveTabulation(vector<int>& days, vector<int>& costs)
    {
          vector<int> dp(days.size()+1 , 0);

        for(int k = days.size()-1 ; k>=0 ; k--)
        {
             //1 day Pass
        int op1 = costs[0] + dp[k+1];

        //7 Day pass
        int i = k+1 ;
        while( i<days.size() && days[i]-days[k]<7 )
            i++;

        int op2 = costs[1] + dp[i];

        //7 Day pass
         while( i<days.size() && days[i]-days[k]<30 )
             i++;

        int op3 = costs[2] + dp[i];

            dp[k] =  min(op1 , min(op2, op3));
        }

        return dp[0];
    }

    int SolveByQueue(vector<int>& days, vector<int>& costs)
    {
        int ans  = 0;

        queue<pair<int, int>> week;

        queue<pair<int, int>> month;

