class Solution {
public:
	int longestSubarray(vector<int>& nums, int limit) {
		if(nums.empty()) return 0;
		if(nums.size()==1) return 1; //corner cases
		int n=nums.size();
		int curMAX=nums[0];
		int curMIN=nums[0];
		int ans=0;
		int left=0; // left pointer
		for(int i=1;i<nums.size();i++)
		{
			curMAX=max(curMAX,nums[i]); // current max value in the window
			curMIN=min(curMIN,nums[i]); // current min value in the window
			if(abs(nums[i]-curMIN)>limit || abs(curMAX-nums[i])>limit) // when nums[i] breaks the window
			{
				int temp=i;
				left=temp;
				curMAX=nums[i];       // reset max value
				curMIN=nums[i];       // reset min value
				while(abs(nums[i]-nums[temp])<=limit) // brute force to find left pointer
				{
					left=temp;
					temp--;
					curMAX=max(curMAX,nums[left]); // update max value in the new window 
					curMIN=min(curMIN,nums[left]); // update min value in the new window
				}
			}
			ans=max(ans,i-left+1);
		}
		return ans;
	}
};
```

