                if(minq.First.Value == nums[l]) minq.RemoveFirst();
                if(maxq.First.Value == nums[l]) maxq.RemoveFirst();
                l++;
            }

            max = Math.Max(max,r-l+1);
            r++;
        }

        return max;
    }
}
