#include <cassert>
#include <deque>

class Solution {
public:
    int longestSubarray(vector<int>& nums, int limit) {
        assert(limit >= 0);
        std::size_t longest = 1;
        std::deque<std::size_t> maxDeque;
        std::deque<std::size_t> minDeque;
        for (std::size_t left = 0, right = 0; right < nums.size(); ++right) {
            while (!maxDeque.empty() and nums[maxDeque.back()] <= nums[right]) {
                maxDeque.pop_back();
            }
            maxDeque.push_back(right);

            while (!minDeque.empty() and nums[minDeque.back()] >= nums[right]) {
                minDeque.pop_back();
            }
            minDeque.push_back(right);

            while (nums[maxDeque.front()] - nums[minDeque.front()] > limit) {
                if (left == maxDeque.front()) {
                    maxDeque.pop_front();
                }
                if (left == minDeque.front()) {
                    minDeque.pop_front();
                }
                ++left;
            }
            longest = std::max(longest, right - left  + 1);
        }
        return longest;
    }
};
