**Approach:** nearest greater on right and nearest smaller on right

```
class Solution {
public:

	// to store index of nearest greater element on right side
    vector<int> greater(vector<int>& a){
        int n=a.size();
        vector<int> ngr;
        stack<pair<int,int>> st;
        for(int i=n-1;i>=0;i--){
            while(st.size()>0 && st.top().first<a[i]) st.pop();
            if(st.size()==0){
                ngr.push_back(n);
            }else{
                ngr.push_back(st.top().second);
            }
            st.push({a[i],i});
        }
        reverse(ngr.begin(),ngr.end());
        return ngr;
    }

	// to store index of nearest smaller element on right side
    vector<int> smaller(vector<int>& a){
        int n=a.size();
        vector<int> nsr;
        stack<pair<int,int>> st;
        for(int i=n-1;i>=0;i--){
            while(st.size()>0 && st.top().first>a[i]) st.pop();
            if(st.size()==0){
                nsr.push_back(n);
            }else{
                nsr.push_back(st.top().second);
            }
            st.push({a[i],i});
        }
        reverse(nsr.begin(),nsr.end());
        return nsr;
    }

    int longestSubarray(vector<int>& a, int k) {
        int n=a.size();

        vector<int> ngr=greater(a);
        vector<int> nsr=smaller(a);

        int i=0, j=0;
        int maxlen=0;
        int maxIdx=0, minIdx=0;
        while(j<n){
            if(a[j]>a[maxIdx]) maxIdx=j;
            if(a[j]<a[minIdx]) minIdx=j;

            if(i<=j && abs(a[maxIdx]-a[minIdx])>k){
                i++;
                int len=j-i+1;

                // max in window
                maxIdx=max(maxIdx,i);
                while(ngr[maxIdx]<i+len) maxIdx=ngr[maxIdx];

                // min in window
                minIdx=max(minIdx,i);
                while(nsr[minIdx]<i+len) minIdx=nsr[minIdx];
            }
            int len=j-i+1;
            maxlen=max(maxlen,len);
            j++;
        } 
        return maxlen;
    }
};
```

