class Solution {
    public int longestSubarray(int[] nums, int limit) {
        //Sliding window
        if (nums.length == 1) return 1;

        int size = 2;
        PriorityQueue<Integer> minQ = new PriorityQueue<Integer>();
        PriorityQueue<Integer> maxQ = new PriorityQueue<Integer>(Collections.reverseOrder());
        minQ.add(nums[0]);
        maxQ.add(nums[0]);

        int min = 0;
        int max = 0;

        for (int i = 0, j = i + 1; j < nums.length;) {
            minQ.add(nums[j]);
            maxQ.add(nums[j]);
            min = minQ.peek();
            max = maxQ.peek();
            if (Math.abs(max - min) <= limit) {
                j++;
                size = Math.max(size, j - i);

            }
            else {
                minQ.remove(nums[i]);
                maxQ.remove(nums[i]);
                i++;
                j++;
            }
        }
        return size;
    }
}
