class Solution {
public:
    int longestSubarray(vector<int>& nums, int limit) {
        int res=0;
        map<int,int> m; // value ->count
        for(int l=0,r=0;r<nums.size();r++) {
            int v=nums[r];
            m[v]++;
            while(m.rbegin()->first - m.begin()->first > limit) {
                v=nums[l++];
                m[v]--; if(m[v]==0) m.erase(v);
            }
            res=max(res,r-l+1);
        }
        return res;
    }
};
