class Solution {
    public int longestSubarray(int[] nums, int limit) {
        int left = 0;
        int right = 1;
        int max = nums[0];
        int min = nums[0];
        int res = 1;
        while(right < nums.length){
            if(nums[right] >= max - limit && nums[right] <= min + limit){
                max = Math.max(max, nums[right]);
                min = Math.min(min, nums[right]);
            }else{
                res = Math.max(right - left, res);
                int index = right - 1;
                max = nums[right];
                min = nums[right];
                while(index >= left && nums[index] >= max - limit && nums[index] <= min + limit){
                    max = Math.max(max, nums[index]);
                    min = Math.min(min, nums[index]);
                    index--;
                }
                left = index+1;
            }
            right++;
        }
        res = Math.max(right - left, res);
        return res;

    }
}
