class Solution 
{
public:
    int longestSubarray(vector<int>& nums, int limit) 
    {
        int l = nums.size();

        //for storing minimum element.
        priority_queue<pair<int,int>,vector<pair<int,int>>,greater<pair<int,int>>> minn;

        //for storing maximum element.
        priority_queue<pair<int,int>> maxx;

        int st = 0; //starting index of subarray.
        int res = 1;

        minn.push({nums[0],0});
        maxx.push({nums[0],0});
        for(int i=1;i<l;i++)
        {
            //excluding elements which are not part of current sub array.
            while(!minn.empty() && minn.top().second < st)
                minn.pop();

            while(!maxx.empty() && maxx.top().second < st)
                maxx.pop();

            //if current index is start index.
            if(minn.empty() || maxx.empty())
            {
                minn.push({nums[i],i});
                maxx.push({nums[i],i});
                continue;
            }

            int mx = maxx.top().first;
            int mn = minn.top().first;

            //if absolute difference of element with max and min element is in the range than differance with other elements are also in the given range.
            if(abs(mx-nums[i]) <= limit && abs(mn-nums[i]) <= limit)
            {
                res = max(res,i-st+1);
                minn.push({nums[i],i});
                maxx.push({nums[i],i});
            }else
            {
                if(abs(mx-nums[i])>limit)
                    st = maxx.top().second+1;
                if(abs(mn-nums[i])>limit)
                    st = max(st,minn.top().second+1);

                //for checking current number again.
                i--;
            }
        }

        return res;
    }
};
