class Solution {
    public int longestSubarray(int[] nums, int limit) {
        int j = -1,res = 0,n = nums.length;
        ArrayDeque<Integer> min = new ArrayDeque<>();
        ArrayDeque<Integer> max = new ArrayDeque<>();
        for(int i = 0;i<n;i++){
            while(!min.isEmpty() && nums[i] <= nums[min.peekLast()]) min.removeLast();
            while(!max.isEmpty() && nums[i] >= nums[max.peekLast()]) max.removeLast();
            min.addLast(i);
            max.addLast(i);
            while((nums[max.peekFirst()]-nums[min.peekFirst()]) > limit){
                if(max.peekFirst() < min.peekFirst()) j=max.removeFirst();
                else j = min.removeFirst();
            }
            res = Math.max(res,i-j);
        }
        return res;
    }
}
