class Solution {
public:
    int longestSubarray(vector<int>& nums, int limit) {
        int n=nums.size();
        deque<pair<int, int>> max_dq, min_dq;

        int left=0, ans=0;
        for(int right=0; right<n; right++){
            while(!max_dq.empty() && max_dq.back().first<=nums[right])
                max_dq.pop_back();

            while(!min_dq.empty() && min_dq.back().first>=nums[right])
                min_dq.pop_back();

            max_dq.push_back({nums[right], right});
            min_dq.push_back({nums[right], right});

            while(max_dq.front().first-min_dq.front().first>limit){
                left++;
                while(!max_dq.empty() && max_dq.front().second<left)
                    max_dq.pop_front();

                while(!min_dq.empty() && min_dq.front().second<left)
                    min_dq.pop_front();
            }

            ans = max(ans, right-left+1);
        }
        return ans;
    }
};
```

