public class Solution {
    public int LongestSubarray(int[] nums, int limit) {
        var left=0;
        var idx=0;
        var size = 0;
        var map = new MinMaxDictionary();

        while(idx < nums.Length) {
            // Add the element in the map
            map.Add(nums[idx]);

            // Get min and max values
            int minWin = map.Min();
            int maxWin = map.Max();

            // Check if the absolute diff is within the limit, 
            // else move the windows one element to the right
            if(Math.Abs(minWin-maxWin) <= limit) {
                size = Math.Max(size, idx-left+1);
            } else {
                map.Remove(nums[left]);
                left++;
            }

            idx++;
        }

        return size;
    }

    // Helper class to get the min and max in the dictionary
    public class MinMaxDictionary {
        public Dictionary<int, int> map;

        public MinMaxDictionary() {
            map = new Dictionary<int, int>();
        }

        public int Min() {
            return map.Keys.Min();
        }

        public int Max() {
            return map.Keys.Max();
        }

        public void Add(int val) {
            if(!map.ContainsKey(val))
                map.Add(val, 0);
            map[val]++;
        }

        public void Remove(int val) {
            if(map.ContainsKey(val)) {
                map[val]--;
                if(map[val] == 0)
                    map.Remove(val);
            }

        }
    }
}
