    public static int longestSubarray2(int[] nums, int limit) {
        int longestSeq = 0;
        int seq = 0;
        int min = nums[0], max = nums[0];
        int minIndex = 0, maxIndex = 0;
        for (int i = 0; i < nums.length; i++) {
            if (min >= nums[i]) {
                min = nums[i];
                minIndex = i;
            }
            if (max <= nums[i]) {
                max = nums[i];
                maxIndex = i;
            }
            if (Math.abs(min - max) <= limit) {
                seq++;
            }
            else {
                longestSeq = Math.max(longestSeq, seq);
                // The sequence is broken due to min or max value.
                // So starting from the next number from the minimum index of these two so that we don't miss any sequence.
                i = Math.min(minIndex, maxIndex);

                // Resetting variables.
                seq = 0;
                min = max = nums[i + 1];
                minIndex = maxIndex = i + 1;
            }
        }
        return Math.max(longestSeq, seq);
    }

