class Solution {
public:
    int longestSubarray(vector<int>& nums, int limit){

        int res=1;
        map<int,int> m;
        int i=0,j=1;
        m[nums[0]]++;
        int n=nums.size();
        while(j<n){
            m[nums[j]]++;
            int first=m.begin()->first;
            int last=m.rbegin()->first;
            if((last-first)<=limit){
                res=max(res,j-i+1);
                j++;
            }
            else{
                while(i<j&&(last-first)>limit){
                    m[nums[i]]--;
                    if(m[nums[i]]==0) m.erase(nums[i]);
                    first=m.begin()->first;
                    last=m.rbegin()->first;
                    i++;
                }
                j++;
            }
        }
        return res;

    }
};
