class Solution {
public:
    int longestSubarray(vector<int>& nums, int limit) {
        // In map the keys are stored in sorted order
        // here key is the array element 
        // value is the frequency of the key in current window
        map<int,int>dict;

        // 'i' is the left pointer and 'j' is the right pointer of the window
        // 'ans' will hold the answer
        int i=0,j=0,ans=0;
        while(j<nums.size())
        {
            dict[nums[j]]++;

            // dict.rbegin() returns an iterator which points to the maximum element in the map
            // similarly dict.begin() returns an iterator which points to the minimum element in the map
            // so their difference would give us the range of elements in the window. Keep on shrinking the window as long as the range is greater than limit
            while(dict.rbegin()->first-dict.begin()->first>limit)
            {
                if(dict[nums[i]]==1)
                {
                    dict.erase(nums[i]);
                }
                else
                {
                    dict[nums[i]]--;
                }
                i++;
            }

            // Take the maximum of the current window size and previous sizes
            ans=max(ans,j-i+1);

            // Grow the window
            j++;
        }

        // Finally return the answer
        return ans;
    }
};
