from collections import deque
from typing import Deque

class Solution:
    def longestSubarray(self, nums: List[int], limit: int) -> int:
        max_queue: Deque = deque()
        min_queue: Deque = deque()    
        left: int = 0    
        max_len: int = 0    

        for right in range(len(nums)):
            self.pushing_max(max_queue, nums[right])
            self.pushing_min(min_queue, nums[right])
            while left <= right and max_queue and min_queue and max_queue[0] - min_queue[0] > limit:
                if max_queue[0] == nums[left]:
                    max_queue.popleft()
                if min_queue[0] == nums[left]:
                    min_queue.popleft()
                left += 1    
            max_len = max(max_len, right - left + 1)    
        return max_len    

    def pushing_max(self, max_queue: Deque, value: int):
        while max_queue and max_queue[-1] < value:
            max_queue.pop()
        max_queue.append(value)    

    def pushing_min(self, min_queue: Deque, value: int):
        while min_queue and min_queue[-1] > value:
            min_queue.pop()
        min_queue.append(value)    

