```class Solution {
    public int longestSubarray(int[] nums, int limit) {
        int ans = 0;
        int n = nums.length;

        PriorityQueue<Integer> asc = new PriorityQueue<>((a,b)->a-b);
        PriorityQueue<Integer> dsc = new PriorityQueue<>((a,b)->b-a);

        Integer[] arr = new Integer[n];
        for(int i = 0; i < n; i++)
            arr[i] = nums[i];

        int head = 0;
        int tail = 0;

        while(tail < n){
            asc.add(arr[tail]);
            dsc.add(arr[tail]);

            while(dsc.peek() - asc.peek() > limit){
                asc.remove(arr[head]);
                dsc.remove(arr[head]);
                head++;
            }

            ans = Math.max(ans, tail-head+1);
            tail++;
        }
        return ans;
    }
}

