class Solution {
public:
    int longestSubarray(vector<int>& nums, int limit) {

        std::deque<pair<int, int>> max_deque;
        std::deque<pair<int, int>> min_deque;

        int start_window = 0;
        int end_window = -1;
        int maxLen = 0;
        int K = limit;

        for (int i = 0; i < nums.size(); i++) {        

            while (!max_deque.empty() &&
                   max_deque.back().second < nums[i]) {
                max_deque.pop_back();
            }

            max_deque.push_back({i, nums[i]});

            while (!min_deque.empty() &&
                   min_deque.back().second > nums[i]) {
                min_deque.pop_back();
            }

            min_deque.push_back({i, nums[i]});

            if (max_deque.front().first == i) {

                // Found a new max. Start popping from the
                // min deque till the difference between max
                // and min becomes <= K.

                while (!min_deque.empty() &&
                       (max_deque.front().second - min_deque.front().second) > K) {

					// Start window index will now be the index after the element that
					// was causing the difference between max and min to be > K.
                    start_window = min_deque.front().first + 1;
                    min_deque.pop_front();
                }

            } else if (min_deque.front().first == i) {

                // Found a new min. Start popping from the
                // max deque till the difference between max
                // and min becomes <= K.

                while (!max_deque.empty() &&
                       (max_deque.front().second - min_deque.front().second) > K) {

					// Start window index will now be the index after the element that
					// was causing the difference between max and min to be > K.
                    start_window = max_deque.front().first + 1;
                    max_deque.pop_front();
                }
            }

            end_window++;

            if ((end_window - start_window + 1) > maxLen) {
                maxLen = end_window - start_window + 1;
            }
        }

        return maxLen;
    }
};
