class Solution {

    private void decreaseInMap(Integer key, TreeMap<Integer, Integer> map) {
        if (!map.containsKey(key)) {
            return;
        }
        int value = map.get(key);
        if (value == 1) {
            map.remove(key);
        } else {
            map.put(key, value - 1);
        }
    }

    private void incrementInMap(Integer key, TreeMap<Integer, Integer> map) {
        map.put(key, map.getOrDefault(key, 0) + 1);
    }

    public int longestSubarray(int[] nums, int limit) {
        int left = 0;
        int right = 0;
        int diff = 0;
        int ans = 0;
        TreeMap<Integer, Integer> heap = new TreeMap<Integer, Integer>();
        while (right < nums.length) {
            incrementInMap(nums[right], heap);
            diff = Math.abs(heap.firstKey() - heap.lastKey());

            while (left <= right && diff > limit) {
                int toRemove = nums[left];
                decreaseInMap(toRemove, heap);
                diff = Math.abs(heap.firstKey() - heap.lastKey());
                ++left;
            }

            if (diff <= limit) {
                ans = Math.max(ans, right - left + 1);
            }
            ++right;
        }
        return ans;
    }
}
