/**
 * @param {number[]} nums
 * @param {number} limit
 * @return {number}
 */
var longestSubarray = function(nums, limit) {
    let result = 0;
    const maxDeque = new Deque();
    const minDeque = new Deque();
    let left = -1;
    for (let right = 0; right < nums.length; right++) {
        const value = nums[right];
        while (!maxDeque.isEmpty() && nums[maxDeque.first()] - value > limit) {
            left = Math.max(left, maxDeque.dequeue());
        }
        while (!minDeque.isEmpty() && value - nums[minDeque.first()] > limit) {
            left = Math.max(left, minDeque.dequeue());
        }
        result = Math.max(result, right - left);
        while (!minDeque.isEmpty() && nums[minDeque.last()] > value) {
            minDeque.pop();
        }
        while (!maxDeque.isEmpty() && nums[maxDeque.last()] < value) {
            maxDeque.pop();
        }
        maxDeque.enqueue(right);
        minDeque.enqueue(right);
    }
    return result;
};

class Deque {
    constructor() {
        this.head = new Node();
        this.tail = new Node();
        this.head.next = this.tail;
        this.tail.prev = this.head;
    }

    isEmpty() {
        return this.head.next === this.tail;
    }

    first() {
        const { next } = this.head;
        if (next === this.tail) return null;
        return next.index;
    }

    last() {
        const { prev } = this.tail;
        if (prev === this.head) return null;
        return prev.index;
    }

    enqueue(index) {
        const node = new Node(index);
        const { prev } = this.tail;
        prev.next = node;
        node.prev = prev;
        node.next = this.tail;
        this.tail.prev = node;
    }

    dequeue() {
        const node = this.head.next;
        if (node === this.tail) return null;
        const { next } = node;
        this.head.next = next;
        next.prev = this.head;
        node.next = node.prev = null;
        return node.index;
    }

    pop() {
        const node = this.tail.prev;
        if (node === this.head) return null;
        const { prev } = node;
        prev.next = this.tail;
        this.tail.prev = prev;
        node.next = node.prev = null;
        return node.index;
    }
}

class Node {
    constructor(index) {
        this.index = index;
        this.prev = this.next = null;
    }
}
