    int longestSubarray(vector<int>& nums, int limit) {
        int n=nums.size();
        deque<int>miny;
        deque<int>maxy;
        int s=0;
        int e=0;
        int ans=0;
        while(e<n){
            int x=nums[e];
         //   cout<<x<<endl;
            while(!miny.empty() && nums[miny.back()]>=x){
                miny.pop_back();
            }
            while(!maxy.empty() && nums[maxy.back()]<=x){
                maxy.pop_back();
            }

            miny.push_back(e);
            maxy.push_back(e);

            int dif=nums[maxy.front()]-nums[miny.front()];
            if(dif>limit){
                s++;
                while(!miny.empty() && miny.front()<s){
                    miny.pop_front();
                }
                while(!maxy.empty() && maxy.front()<s){
                    maxy.pop_front();
                }


            }
            else{
                ans=max(ans,e-s+1);
                e++;
            }




        }
        return ans;

    }
};
