public:
    int longestSubarray(vector<int>& nums, int limit) {
        int i = 0;
        int j = 0;
        priority_queue<pair<int,int>> pqMax;
        priority_queue<pair<int,int>,vector<pair<int,int>>,greater<pair<int,int>>> pqMin;
        int ans = 1;
        while(i<nums.size() && j<nums.size()){
            pqMax.push({nums[j],j});
            pqMin.push({nums[j],j});
            if(pqMax.top().first-pqMin.top().first>limit){
                ans = max(ans,j-i);
                while(pqMax.size() && pqMax.top().second<=i)
                    pqMax.pop();
                 while(pqMin.size() && pqMin.top().second<=i)
                    pqMin.pop();
                i++;
            }
            j++;
        }
        ans = max(ans,j-i);
        return ans;
    }
};

