class Solution {
    public int longestSubarray(int[] nums, int limit) {

        if(nums.length == 0) return 0;
        if(nums.length == 1) return limit == 0 ? 0 : 1;
        int left =  0;
        int right = 0; 
        int min = nums[0];
        int max = nums[0];
        // PriortyQueue<Integer> min_heap = new PriorityQueue<Integer>();
        // PriortyQueue<Integer> max_heap = new PriorityQueue<Integer>((a,b) -> b - a);

        int res = 0;

        while(right < nums.length - 1) {
            right++; 
            min = Math.min(min, nums[right]);
            max = Math.max(max, nums[right]);
            int diff = Math.abs(max - min);
            if (diff <= limit) {
                res = Math.max(res, right - left + 1);
             } else {
                int cur_left = left;
                     int cur_min = nums[right];
                     int cur_max = nums[right]; 
                 for (int i = right; i >= cur_left; i--) {
                    if (Math.abs(nums[right] - nums[i]) <= limit) {
                        cur_min = Math.min(cur_min, nums[i]);
                        cur_max = Math.max(cur_max, nums[i]);
                    } else {
                        left = i + 1;
                        min = cur_min; 
                        max = cur_max;
                        break;
                    }
                 }
            }      
        } 
        return res; 
    }
}
