class Solution {
public:
    int longestSubarray(vector<int>& nums, int limit) {
        int mini = INT_MAX-limit;
        int maxi = INT_MIN+limit;
        int best = INT_MIN;
        int start = -1;
        for(int i=0; i<nums.size(); i++){
            if(nums[i] <= mini+ limit && nums[i] >= maxi-limit){
                mini = min(mini, nums[i]);
                maxi = max(maxi, nums[i]);
                best = max(best, i - start);
            }else if(nums[i] < maxi - limit){
                mini = nums[i];
                maxi = INT_MIN+limit;
                start = i-1;
                while(nums[start] >= nums[i] && nums[start]<= nums[i] + limit){
                    mini = min(mini, nums[start]);
                    maxi = max(maxi, nums[start]);
                    start--;
                }
            }else{
                mini = INT_MAX-limit;
                maxi = nums[i];
                start = i-1;
                while(nums[start] <= nums[i] && nums[start]>= nums[i] - limit){
                    mini = min(mini, nums[start]);
                    maxi = max(maxi, nums[start]);
                    start--;
                }
            }
        }
        return best;
    }
};
