class Solution {
    Set<Character> vowelsSet = Set.of('a', 'e', 'i', 'o', 'u');
    public int maxVowels(String s, int k) {
        var vowelsCount = 0;

        for (var i = 0; i < k; ++i) {
            if (vowelsSet.contains(s.charAt(i))) {
                ++vowelsCount;
            }
        }
        var res = vowelsCount;
        for (var i = k; i < s.length(); ++i) {
            if (vowelsSet.contains(s.charAt(i))) {
                ++vowelsCount;
            }
            if (vowelsSet.contains(s.charAt(i-k))) {
                --vowelsCount;
            }
            res = Math.max(res, vowelsCount);
        }
        return res;
    }
}
