        if (k >= s.length()) {
            return count;
        }

        // Set the start and end indices for the sliding window
        int start = 0;
        int end = k;

        // Update the maximum count
        max_count = max(max_count, count);

        // Slide the window through the remaining characters
        while (end < s.length()) {
            // Check if the character at the start index is a vowel and decrement the count if necessary
            if (vowel.find(word[start]) != string::npos) {
                count--;
            }

            // Check if the character at the end index is a vowel and increment the count if necessary
            if (vowel.find(word[end]) != string::npos) {
                count++;
