const VOWELS = ['a', 'e', 'i', 'o', 'u'];

/**
 * @param {string} s
 * @param {number} k
 * @return {number}
 */
var maxVowels = function(s, k) {
    let max = 0;
    let counter = 0;

    // go through the string, but the last index should be s.length - k, 
    // as i represents the start index of the substring with length k"
    for (let i = 0; i < s.length - k + 1; i++) {

        // if it's the start of the string, then we need to define our counter for the first time
        // so we won't need to calculate it over and over again later
        if (i === 0) {
            for (let j = 0; j < k; j++) {
                if (VOWELS.includes(s[j])){
                    counter++;
                }
            }
        // if it's the rest of the string, then we just update the counter by analyzing 
        // the removed first character of the string and the last added character
        } else {
            // if the removed first character was vowel then we decrease counter
            if (VOWELS.includes(s[i - 1])) {
                counter--;
            }
            // if the last added character is vowel then we increase counter
            if (VOWELS.includes(s[i + k - 1])) {
                counter++;
            }
        }

        // if the counter is greater than the max, we update the max value
        if (counter > max) {
            max = counter; 
        }
        // if the max value is equal to k, we return the result, as the max value cannot be greater than k.
        if (max === k) {
            return max;
        }
    }
    return max;
};
