class Solution {
    public int maxVowels(String s, int k) {
       int vowel = 0, count = 0;

        // Initialize cumulative vowel count for the first k characters
        for (int i = 0; i < k; i++) {
            char c = s.charAt(i);
            if (c == 'a' || c == 'e' || c == 'i' || c == 'o' || c == 'u') {
                vowel++;
            }
        }
        count = vowel;

        // Update cumulative vowel count for each subsequent window of size k
        for (int i = k; i < s.length(); i++) {
            char c1 = s.charAt(i-k), c2 = s.charAt(i);
            if (c1 == 'a' || c1 == 'e' || c1 == 'i' || c1 == 'o' || c1 == 'u') {
                vowel--;
            }
            if (c2 == 'a' || c2 == 'e' || c2 == 'i' || c2 == 'o' || c2 == 'u') {
                vowel++;
            }
            count = Math.max(count, vowel);
        }

        return count;


    }
}
