        int max = count;

        int end = k-1;
        int start = 0;

        while(end<length-1){
            if(isVowel(s.charAt(start)) && count>0) count--;
            if(isVowel(s.charAt(end+1))) count++;
            start++;
            end++;
            max = Math.max(count,max);
        }

        return max;

    }


