C++ Simple and real optimized solution Time complexity O(n) and Space complexity O(1). Without using any extra space. 


class Solution {
public:
    bool isVowel(char c)
    {
        if(c == 'a' || c == 'e' || c == 'i' || c == 'o' || c == 'u')
        return true;
       return false;
    }
    int maxVowels(string s, int k) {

        int count = 0,maxcount = 0;
        for(int i=0;i<k;i++)
        {
           if(isVowel(s[i]))
           count++;
        }
        maxcount = count;

        for(int i=0;i<s.length() - k; i++)
        {
            if(!isVowel(s[i]) && isVowel(s[i+k]))
            {
                count = count + 1;
            }
            else if(isVowel(s[i]) && !isVowel(s[i+k]))
            {
                count = count - 1;
            }
            else{

                count = count;
            }
            maxcount = max(maxcount,count);
        }
        return maxcount;
    }
};

//using program in c#...
public class Solution {
    public int MaxVowels(string s, int k) {
        int i,count=0,max=0;

       for(i=0;i< s.Length;i++){
           count = count + IsVowel(s[i]);
           if(i >= k){
               count = count - IsVowel(s[i - k]);               
           }
           max = Math.Max(max , count);
       }
       return max;
    }
    public int IsVowel(char ch){
        if (ch == 'a' || ch == 'e' || ch == 'i' || ch == 'o' || ch == 'u')
        {
            return 1;
        }
        else
        {
            return 0;
        }
    }
}

