class Solution:
    def maxVowels(self, s: str, k: int) -> int:
        suffix = []
        result = 0
        vowels = set("aeiou")
        if s[0] in vowels:
            suffix.append(1)
        else:
            suffix.append(0)
        length = len(s)
        for i in range(1,length):
            if s[i] in vowels:
                suffix.append(1+suffix[-1])
            else:
                suffix.append(suffix[-1])
        if length==k:
            return suffix[-1]
        maximum = 0
        for i in range(length-k+1):
            if i==0:
                maximum = max(maximum,suffix[i+(k-1)])
            else:
                maximum = max(maximum,suffix[i+(k-1)]-suffix[i-1])
        return maximum
