class Solution {
public:
    bool isVowel(char& c){
        return c == 'a' || c == 'e' || c == 'i' || c == 'o' || c == 'u';
    }

    int maxVowels(string s, int k) {
        int n = s.size();
        int maxct = 0;
        int ct = 0;

        // Count vowels in first substring of length k
        for (int i = 0; i < k; i++) {
            if (isVowel(s[i])) {
                ct++;
            }
        }
        maxct = ct;

        // Slide the window of size k over the string s
        for (int i = k; i < n; i++) {
            // Subtract the count of vowel at the start of the previous window
            if (isVowel(s[i - k])) {
                ct--;
            }
            // Add the count of vowel at the end of the current window
            if (isVowel(s[i])) {
                ct++;
            }
            // Update the maximum count of vowels found so far
            maxct = max(maxct, ct);
        }
        return maxct;
    }
};
