var pseudoPalindromicPaths  = function(root) {
    if (!root) return 0;
    const q = []; let result = 0;
    q.push(root);
    root.c = 1 << root.val;
    while(q.length) {
        const cur = q.shift();
        if (cur.left) {
            cur.left.c = cur.c ^ (1 << cur.left.val);
            q.push(cur.left);
        }
        if (cur.right) {
            cur.right.c = cur.c ^ (1 << cur.right.val);
            q.push(cur.right);
        }
        if (!cur.left && ! cur.right) result += (cur.c & (cur.c - 1)) > 0 ? 0 : 1;
    }
    return result;
};
