            if(root.left == None and root.right == None):
                if(isPalindromicPath(arr)): count = 1

            if(root.left != None): count += calcPalindromicPaths(root.left, arr)
            if(root.right != None): count += calcPalindromicPaths(root.right, arr)

            arr[root.val] -= 1
            return count; 


