class Solution {

    public int res = 0;
    public int pseudoPalindromicPaths (TreeNode root) {

        Set<Integer> set = new HashSet<>();
        util(root, set);
        return res;
    }

    public void util(TreeNode root, Set<Integer> set){
        if(root == null) return;

        if(set.contains(root.val)) set.remove(root.val);
        else set.add(root.val);

        if(root.left == null && root.right == null){
            if(set.size() == 1 || set.isEmpty()) res++;
        }

        util(root.left, set);
        util(root.right, set);

        if(set.contains(root.val)) set.remove(root.val);
        else set.add(root.val);

    }

}
```

