class Solution {
public:

    int solve(TreeNode* root, map<int,int> &mp){
        if(root == NULL) return 0;
        if(root->left == NULL && root->right == NULL){
            mp[root->val]++;
            int x = 0; // keeping track of how many elements occur odd number of times
            for(auto it:mp){
                if(it.second % 2 != 0) x++; // checking odd occurrences
            }
            mp[root->val]--; // backtracking
            if(mp[root->val] == 0) mp.erase(root->val);
            if(x > 1) return 0; // if its a pallindrome then only one element can be in odd frequency
            return 1;
        }
        mp[root->val]++;
        int l = solve(root->left,mp); // calculating how many paths are valid in left subtree
        int r = solve(root->right,mp); // calculating how many paths are valid in right subtree
        mp[root->val]--; // backtracking
        if(mp[root->val] == 0) mp.erase(root->val); // removing from the map if frequency reaches 0
        return l + r; // returning all the possible cases from both the subtree
    }

    int pseudoPalindromicPaths (TreeNode* root) {
        map<int,int> mp;
        return solve(root,mp);
    }
};
