    def pseudoPalindromicPaths (self, root: Optional[TreeNode]) -> int:
        def pre(root,dic):
            if root is None:
                return 0
            if root.left is None and root.right is None:
                if root.val not in dic:
                    dic[root.val]=1
                else:
                    dic[root.val]+=1
                fg=0
                for i in dic:
                    if dic[i]%2==1 and fg==1:
                        return 0
                    if dic[i]%2==1:
