public int PseudoPalindromicPaths(TreeNode root)
{
	return Helper(root, 0);
}

//Since node Val is 1-9, we can use 9-bit binary number to represent the path.
//Use XOR to cancel out Val pairs.
//The path is palindrome if there is zero or one *1* left in bit, bit & (bit-1) can simplify the check. 
private int Helper(TreeNode node, int bit)
{
	if (node == null)   
		return 0;
	bit ^= 1 << node.val; 
	if(node.left == null && node.right == null)
		return (bit & (bit-1)) == 0 ? 1 : 0;
	return Helper(node.left, bit) + Helper(node.right, bit);
}
