            if(not node.left and not node.right and isAnagramOfPalindrome(path+ [node.val])):
                self.ans += 1
            dfs(node.left, path + [node.val])
            dfs(node.right, path + [node.val])
        self.ans = 0
        dfs(root, [])
        return self.ans  

