/**
 * Definition for a binary tree node.
 * function TreeNode(val, left, right) {
 *     this.val = (val===undefined ? 0 : val)
 *     this.left = (left===undefined ? null : left)
 *     this.right = (right===undefined ? null : right)
 * }
 */
/**
 * @param {TreeNode} root
 * @return {number}
 */
var pseudoPalindromicPaths  = function(root) {
    if (!root.left && !root.right) {
        return 1;
    }

    let count = { count: 0 };
    process(root, {}, count);

    return count.count;
};

function process(root, values, count) {
    if (root && !root.left && !root.right) {
        if (!values[root.val]) {
            values[root.val] = 0;
        }
        values[root.val] += 1;

        if (values[root.val] === 2) {
            delete(values[root.val]);
        }

        const length = Object.keys(values).length;
        if (length <= 1) {
            count.count += 1;
        }

        return;
    } else if (!root) {
        return;
    }

    if (!values[root.val]) {
        values[root.val] = 0;
    }
    values[root.val] += 1;

    if (values[root.val] === 2) {
        delete(values[root.val]);
    }

    process(root.left,  Object.assign({}, values), count);
    process(root.right, Object.assign({}, values), count);
}
