	int ans = 0;
    set<int> s;

	// Toogle will insert a value in the set if its not already present and will remove otherwise.

    void toggle(TreeNode* root) {
        if(s.find(root->val)!= s.end()) s.erase(root->val);
        else s.insert(root->val);
    }

	// dfs function is the recursive calling of the function for all the nodes to get the path

    void dfs(TreeNode* root) {
        if(root == nullptr) return;
        toggle(root);    // Inserting the node into the path
        if(root->left == nullptr && root->right == nullptr) {
            if(s.size() < 2) ans++; 
            toggle(root);    // Backtracking i.e. removing the node from path
            return;
        }
        dfs(root->left);
        dfs(root->right);
        toggle(root);     // Backtracking i.e. removing the node from path
    }
    int pseudoPalindromicPaths (TreeNode* root) {
        dfs(root);
        return ans;
