class Solution {
public:

    unordered_map<int,int>mp;
    int res=0;
    void helper(TreeNode*root){

        if(root==NULL)return;

        mp[root->val]++;   //store the occrence of every integer into the map

        if(!root->left &&!root->right){
            int oct=0;               //count the number of integers whose occerence is odd
            for(auto&it:mp){
                if((it.second)%2==1)oct++;

                if(oct>1)break;    //if more than one integer have odd occurence then they cant make palindrome

            }

            if(oct<=1)res++;    //if there is only one or zero elment whose occurence is odd then they make pseudo palindrome
        }

        helper(root->left);
        helper(root->right);
        mp[root->val]--;          //as we insert every integer now after this we backtack that remove that integer from map

    }

    int pseudoPalindromicPaths (TreeNode* root) {

        helper(root);
        return res;

    }
};
