class Solution {
    public int pseudoPalindromicPaths (TreeNode root) {
        int[] hash = new int[10];
        int[] ans = new int[1];
        ++hash[root.val];
        dfs(root, hash, ans, 1);
        return ans[0];
    }

    private void dfs(TreeNode root, int[] hash, int[] ans, int count) {
		//count means the number of odd digits
        if (root.left == null && root.right == null) {
            if (count <= 1) ans[0]++;
        }
		// classic backtracking
        if (root.left != null) {
            hash[root.left.val]++; // choose
            dfs(root.left, hash, ans, hash[root.left.val] % 2 == 0 ? count - 1 : count + 1);
            hash[root.left.val]--;  // unchoose
        }
        if (root.right != null) {
            hash[root.right.val]++;
            dfs(root.right, hash, ans, hash[root.right.val] % 2 == 0 ? count - 1 : count + 1);
            hash[root.right.val]--;
        }
    }
}
