public class Solution {

	 static int count[]; 
     // count of the number of such paths
	 static int res = 0;

	 //Logic of checking whether palindorme possible or not
	 private boolean isPossible() {

		 // Check in the given path if more then 1 digit having ODD frequency then answer is not possible
		 int oddCount = 0;

		 for(int i=0; i<10; i++) {

			 if(count[i]%2 != 0) {
				 oddCount++;
			 }
		 }

		 if(oddCount > 1) {
			 return false;
		 }

		 return true;
	 }


	 private void countAns(TreeNode root) {

		 if(root == null) {
			 return;
		 }

		 //When root check whether it forms the solution or not
		 if(root.left == null && root.right == null) {

			 count[root.val]++;

			 //if valid solution then increment the answer
			 if(isPossible()) {
				 res++;
			 }
			 count[root.val]--;
			 return;
		 }

		 //incrementing the digit at particular node
		 count[root.val]++;
		 countAns(root.left);
		 countAns(root.right);
		 //decrementing once visit is finished
		 count[root.val]--;
	 }

	 public int pseudoPalindromicPaths (TreeNode root) {

		 res = 0;

		 if(root == null) {
			 return res;
		 }

		 count = new int[10];
		 countAns(root);
		 return res;
	 }

}


