class Solution 
{
    int ans=0;
    public int pseudoPalindromicPaths (TreeNode root) 
    {
        Set<Integer> list = new HashSet<>();
        solve(root,list);
        return ans;
    }
    void solve(TreeNode root , Set<Integer> set)
    {
        if(root == null)
            return;

        if(set.contains(root.val))
            set.remove(root.val);       //for even frequency
        else
            set.add(root.val);

        if(root.left == null && root.right == null)
        {
            if(set.size() == 1 || set.isEmpty())
                ans++;
        }

        solve(root.left,set);
        solve(root.right,set);

        if(set.contains(root.val))  //in case of backtracking adding that element again.
            set.remove(root.val);
        else
            set.add(root.val);

    }
}
