 *     TreeNode(int x, TreeNode *left, TreeNode *right) : val(x), left(left), right(right) {}
 * };
 */
class Solution {
public:
    int cnt=0;
    bool check(unordered_map<int,int> &m) // for checking palindrome
    {
        long long cnt1=0;
        for(auto it:m)
            cnt1+=it.second; // for calculating no of occurences
        if(cnt1%2==0) // if even length then all elements occurences must be even for satisying palindrome condition
        {
            for(auto it:m)
            {
                if(it.second%2==1)
                    return false;
            }
            return true;
        }
        else // for odd one occurence odd rest all even occurences for satisying palindrome condition
        {
            int c1=0;
            for(auto it:m)
            {
               if(it.second%2==1)
                   c1++;
            }
            if(c1==1)
            return true;
           return false; 
        }
        return false;
    }
    void f(TreeNode* root,unordered_map<int,int> &s)
    {
        if(root==NULL)
            return;
        if(root->left==NULL and root->right==NULL)
        {
           s[root->val]++;
            if(check(s))
                cnt++;
            s[root->val]--;
            return;
        }
        s[root->val]++;
        f(root->left,s);
        f(root->right,s);
        s[root->val]--;
    }
    int pseudoPalindromicPaths (TreeNode* root) {
        unordered_map<int,int> s; // for checking frequency of elements
        int cnt1=0;
        f(root,s);
        return cnt;
    }
};
