class Solution {
    public int pseudoPalindromicPaths (TreeNode root) {
        int[] freq = new int[10];
        return dfs(root, freq);
    }

    private int dfs(TreeNode root, int[] freq) {
        if(root == null) {
            return 0;
        }

        int countPallindrome = 0; 
        freq[root.val]++;
        if(isLeafNode(root) && canFormPallindrome(freq)) {
            countPallindrome++;
        }

        countPallindrome += dfs(root.left, freq);
        countPallindrome += dfs(root.right, freq);
        freq[root.val]--;
        return countPallindrome;
    }

    private boolean canFormPallindrome(int[] freq) {
        int countOdd = 0;
        for(int num : freq) {
            if(num%2 != 0) {
                countOdd++;
            }
        }

        return countOdd > 1 ? false : true;
    }

    private boolean isLeafNode(TreeNode root) {
        return root.left == null && root.right == null;
    }
}
