/**
 * Definition for a binary tree node.
 * function TreeNode(val, left, right) {
 *     this.val = (val===undefined ? 0 : val)
 *     this.left = (left===undefined ? null : left)
 *     this.right = (right===undefined ? null : right)
 * }
 */
/**
 * @param {TreeNode} root
 * @return {number}
 */
 var pseudoPalindromicPaths = function(root) {
    let count = 0
    let superset = []
    var driver = function(r, level, temp) {
        if (!r) return
        temp[level] = r.val
        level += 1
        if (r.right === null && r.left === null) {
            superset.push(temp.slice(0)) // Copy by value and not reference!
            return
        } else {
            driver(r.right, level, temp.slice(0))
            driver(r.left, level, temp.slice(0))
        }
    }
    driver(root, 0, [])
    // Logic to check if any of the array is palindrome
    for (let i of superset) {
        freq = {}
        for (let j in i) {
            freq[i[j]] === undefined ? freq[i[j]] = 1 : freq[i[j]] += 1
        }
        if (i.length % 2 === 1) {
          if((Object.values(freq).filter(fr => fr % 2 === 1).length === 1)){
            count+=1
          }
        } else {
          if((Object.values(freq).filter(fr => fr % 2 === 1).length === 0)){
            count+=1
          }
        }
    }
    return count
};
