public:
    int pseudoPalindromicPaths (TreeNode* root, int bitVec = 0) {
		//check if the current node exist
        if(!root)
            return 0;

		//if the current node is a leaf, we count the 1 bits in the bit vector, if they are greater then one we return 0 due to the fact that the path is not pseudo palindrome
        if(!root->left&&!root->right){
            return __builtin_popcount(bitVec^(1<<root->val))<=1;
        }

		//dfs
        return pseudoPalindromicPaths(root->left,bitVec^(1<<root->val))+pseudoPalindromicPaths(root->right,bitVec^(1<<root->val));	
    }
};
