func dps(node *TreeNode, odds *map[int]int, oddCount int) int {
	if node == nil {
		return 0
	}
	if _, ok := (*odds)[node.Val]; ok {
		(*odds)[node.Val]++
		if (*odds)[node.Val]%2 == 1 {
			oddCount = oddCount - 1
		} else {
			oddCount = oddCount + 1
		}
	} else {
		(*odds)[node.Val] = 1
	}

	res := 0
	if node.Left == nil && node.Right == nil {
		if oddCount >= len(*odds)-1 {
			res = 1
		}
	}
	res += dps(node.Left, odds, oddCount) + dps(node.Right, odds, oddCount)
	(*odds)[node.Val]--
	if (*odds)[node.Val] == 0 {
		delete((*odds), node.Val)
	}
	return res
}

func pseudoPalindromicPaths(root *TreeNode) int {
	odds := make(map[int]int)
	return dps(root, &odds, 0)
}
